using NUnit.Framework;

namespace AoC._06;

public class TuningTroubleTest {
    private const string Input =
        "";

    [Test]
    public void Example1() {
        Assert.AreEqual(7, TuningTrouble.Tune("mjqjpqmgbljsphdztnvjfqwrcgsmlb"));
        Assert.AreEqual(5, TuningTrouble.Tune("bvwbjplbgvbhsrlpgdmjqwftvncz"));
        Assert.AreEqual(6, TuningTrouble.Tune("nppdvjthqldpwncqszvftbrmjlhg"));
        Assert.AreEqual(10, TuningTrouble.Tune("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"));
        Assert.AreEqual(11, TuningTrouble.Tune("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"));
    }

    [Test]
    public void Puzzle1() {
        var result = TuningTrouble.Tune(Input);
        Assert.AreEqual(1909, result);
        Assert.Pass("Puzzle 1: " + result);
    }


    [Test]
    public void Example2() {
        Assert.AreEqual(19, TuningTrouble.Tune("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 14));
        Assert.AreEqual(23, TuningTrouble.Tune("bvwbjplbgvbhsrlpgdmjqwftvncz", 14));
        Assert.AreEqual(23, TuningTrouble.Tune("nppdvjthqldpwncqszvftbrmjlhg", 14));
        Assert.AreEqual(29, TuningTrouble.Tune("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 14));
        Assert.AreEqual(26, TuningTrouble.Tune("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 14));
    }

    [Test]
    public void Puzzle2() {
        var result = TuningTrouble.Tune(Input, 14);
        Assert.AreEqual(3380, result);
        Assert.Pass("Puzzle 1: " + result);
    }
}